INSERT INTO tb_movies (name, description, release_year) VALUES ('Capitão América - O primeiro vingador', 'Steve Rogers é um jovem que aceitou ser voluntário em experiências para cirar um supersoldado. Os militares conseguem transformá-lo em uma arma humana, mas percebem que o supersoldado é valioso demais para colocá-lo em risco na luta contra os nazistas','2011');
INSERT INTO tb_movies (name, description, release_year) VALUES ('Capitã Marvel', 'Este filme é uma aventura totalmente nova de um período nunca visto na história do UCM que acompanha a jornada de Carol Danvers, conforme ela se torna uma das mais poderosas do universo. Enquanto uma guerra galática entre duas raças alienígenas chega à Terra, Danvers se vê junto a um pequeno grupo de aliados bem no meio da ação.','2019');
INSERT INTO tb_movies (name, description, release_year) VALUES ('Homem de Ferro','Quando é capturado em território inimigo, o genial magnata Tony Stark constrói uma armadura de alta tecnologia para escapar. Agora, ele tem a missão de salvar o mundo como um herói que não nasceu.','2008');
INSERT INTO tb_movies (name, description, release_year) VALUES ('Thor','Como filho de Odin, rei dos deuses nórdicos, Thor logo herdará o trono de Asgard de seu idoso pai. Porém, no dia de sua coroação, Thor reage com brutalidade quando os inimigos dos deuses entram no palácio violando o tratado. Como punição, Odin manda Thor para a Terra. Enquanto seu irmão Loki conspira em Asgard, Thor, agora sem seus poderes, enfrenta sua maior ameaça.','2011');